import React, {useState,memo,useEffect} from 'react'
import './Home.css'

import axios from 'axios';
import useSWR from 'swr';
import Image from '../atoms/Image';
import FlexTemplate from '../templates/FlexTemplate';
import Card from '../molecules/Card';
import Loader from '../atoms/Loader';
import NavBar from '../atoms/NavBar';


const Home = memo(()=>{
  const [newData,changeData]=useState<any[]>([]);
    const CLIENT_ID = process.env.REACT_APP_API_KEY;
    const URL = `https://api.unsplash.com/photos?page=1&per_page=30&client_id=${CLIENT_ID}`

    const {data} = useSWR(URL,{
        refreshInterval: 1000 * 360 *30, 
      });
      const clbck=(searchString:string)=>{
        const {data}= useSWR(`https://api.unsplash.com/search/photos?page=1&query=${searchString}&client_id=${process.env.REACT_APP_API_KEY}`);
        changeData(data);
      }
    useEffect(()=>{
       changeData(data)
        },[data]) 
    if(!data){
          return <Loader/>
        }
return(
  <>
 <NavBar dataToGet={clbck}/>
<FlexTemplate>
{newData?.map(i=>(
    <Card>
    <Image href={i.urls.regular} height={300} width={300} downloadHref={i.links.download}/>
    </Card>
))}
</FlexTemplate>
</>
);
})
export default Home;
