import React,{memo} from 'react'
type imageProps={
    height:number;
    width:number;
    rounded?:boolean;
    href:string;
}
const Image = ({
    height,
    width,
    rounded=false,
href
}:imageProps)=>{
    return(
<div>
    <a href={href} target="_blank" download="image">
    <img src={href} style={{height:`${height}px`, width:`${width}px`, borderRadius:`${rounded ? 50 : 0}%`}}/>
    </a>
</div>)
}

export default memo(Image);
